INSERT INTO `samirxs_category_products` VALUES (1,'SA-01','<p><strong><u>ELPP 1090 S3</u></strong></p>\r\n\r\n<p><strong>Kullanım Alanı:&nbsp;</strong>İş Güvenliği</p>\r\n\r\n<p><strong>Standart: EN ISO 20345 İş Güvenliği Ayakkabıları</strong></p>\r\n\r\n<p><strong>Saya:&nbsp;</strong>Yüksek nefes alabilirlik özellikli cilt deri.&nbsp;</p>\r\n\r\n<p><strong>İç Astar:&nbsp;</strong>Aşınma dayanımı yüksek ter absorbe edebilen ve kısa sürede kuruyan iç astar.</p>\r\n\r\n<p><strong>Taban Teknolojisi:&nbsp;</strong></p>\r\n\r\n<p><strong>-&nbsp;</strong>Yağa dayanıklı dış taban.</p>\r\n\r\n<p><strong>-</strong> Batmaya karşı dayanıklı <strong>\"KEVLAR\"</strong> <strong>ara taban</strong> kullanılmıştır.&nbsp;</p>\r\n\r\n<p><strong>-&nbsp;</strong>Çift yoğunluklu Poliüretan taban teknolojisi. Yüsek konfor sağlayan Poliüretan (PU) orta taban, kaymaya karşı yüksek direnç sağlayan PU dış taban.</p>\r\n\r\n<p><strong>-&nbsp;</strong>Antistatik.</p>\r\n\r\n<p><strong>-&nbsp;</strong>Şok absorbsiyonu ve yumuşaklığı sayesinde kullanıcıya en uygun konforu sağlar.</p>\r\n\r\n<p><strong>-</strong> Kaymazlık <strong>SRC</strong></p>\r\n\r\n<p><strong>Ek Özellikler:</strong>200 J\'e kadar koruma sağlayan<strong>&nbsp;</strong><strong>\"POLICAP\"&nbsp;</strong>burun kullanılmıştır.&nbsp;</p>\r\n\r\n<p><strong>Mostra:&nbsp;</strong>Anatomik olarak şekillendirilmiş, çıkartılabilir,nefes alabilir açık hücre poliüretan mostra.</p>\r\n\r\n<p><strong>Yaklaşık Ağırlık:</strong> 0.55kg</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u><strong>EUROLITES Serisi</strong></u></p>\r\n\r\n<p><strong>YDS Eurolites&nbsp;serisi</strong>, çalışma ortamının risklerini azaltırken rahatlığı en yüksek seviyeye çıkarıyor. Ürünün ömrü, kullanıcının artan verimliliği ve kaza risklerini azaltması ile gerçek maliyet hesaplandığında <strong>EUROLITES</strong> serisi ile <strong>YDS</strong>, kullanıcılara en ekonomik ürünleri sunuyor.</p>','YDS // ELPP 1090 S3 PC'),(2,'SA-02','<p>Upper of <strong>Cordura®</strong> textil and suede leather</p>\r\n\r\n<p>Collar and dust tongue of <strong>Cordura®</strong> textil&nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Extremely breathable <strong>airSurf Fabric</strong> lining</p>\r\n\r\n<p><strong>staBene</strong> last for more comfort&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Removable antistatic anatomically-shaped insole&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p><strong>aluComfort</strong> cap for better ergonomy&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Light and flexible <strong>maxFlex</strong> kevlar midsole&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Antistatic oil-resistant slip-proof <strong>PU-PU sole from BASF®</strong></p>','TALAN // AirLight 367 Yellow-Black');
